import "./style.css";
import { useState, useEffect } from "react";
import document from "../../assets/imgs/document.png";

import axios from "axios";

export default function PostFile() {
  const [srcf, setSrc] = useState("");
  const [message, setMessage] = useState("");
  //let msg = new SpeechSynthesisUtterance();
  const [value, setValue] = useState("");
  useEffect(() => {
    axios.get("/api").then((res) => {
      console.log(res.data.message);
    });
  });
  const change = async (e) => {
    //let text = await e.target.files[0]; //.text();
    setMessage(e.target.files[0]);
    setValue(e.target.value.slice(12));
    setSrc(document);
  };
  const play = async (e) => {
    //msg.text = message;
    //speechSynthesis.speak(msg);
    e.preventDefault();
    console.log(message);
    const formData = new FormData();
    formData.append("file", message);
    const headersb = {
      headers: {
        "Content-type": "multipart/form-data; boundary=XXX",
      },
    };

    await axios
      .post("/extract-text", formData, headersb)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        if (err.res) {
          console.log(err.response);
        } else {
          console.log("o servidor morreu");
        }
      });
  };

  return (
    <div className="Container-post">
      <form onSubmit={play} className="form-area-input">
        <label for="input-fileb" className="label-file">
          Selecione um arquivo aqui
        </label>
        <input
          type="file"
          id="input-fileb"
          className="input-file"
          onChange={change}
          name="file"
        />

        <button className="button-file" type="submit">
          Criar audio
        </button>
      </form>
      {srcf === "" ? (
        <div></div>
      ) : (
        <div className="img-area-file">
          <img src={srcf} className="image-file" alt="documento" />
          <p className="text-file">{value}</p>
        </div>
      )}
    </div>
  );
}
