    speech.text = textmessage;
    return speechSynthesis.speak(speech);
  };
  useEffect(() => {
         changeFile = change();
        
  });*/
  const change = () => {
    //setText(loadfile(this.files[0]));
    return setMessage("arquivo enviado");
  };

  return (
    <div className="Container-post">
      <label for="input-file" className="label-file">
        Selecionar um arquivo
      </label>
      <input
        type="file"
        id="input-file"
        className="input-file"
        onChange={change}
      />
      <span className="text-file">{message}</span>
      <button className="button-file">Criar Arquivo</button>
    </div>
  );
}
