import "./style.css";
import { useState } from "react";

export default function PostFile() {
  const [srcf, setSrc] = useState("");
  const [filein, setFile] = useState("");

  //let speech = new SpeechSynthesisUtterance();

  /*async function loadfile(file) {
    let text = await file.text();
    return text;
  }*/
  /*const speak = () => {
    speech.text = textmessage;
    return speechSynthesis.speak(speech);
  };
  useEffect(() => {
         changeFile = change();
        
  });*/
  const change = (e) => {
    //setText(loadfile(this.files[0]));
    let text = setFile(e.target.files[0]).text();
    console.log(text);

    //setText(filein.text());
    //console.log(textmessage);

    setSrc(
      "https://images.vexels.com/media/users/3/142877/isolated/preview/f71decb4816cd27d4460d37b314d2fbf-documento-de-grafico-plano.png"
    );
  };

  return (
    <div className="Container-post">
      <div className="input-Area">
        <label for="input-fileb" className="label-file">
          Solte ou Selecione um arquivo aqui
        </label>
        <input
          type="file"
          id="input-fileb"
          className="input-file"
          onChange={change}
        />
        {srcf === "" ? (
          <div></div>
        ) : (
          <div className="img-area-file">
            <img src={srcf} className="image-file" alt="documento" />
          </div>
        )}
      </div>
      <div className="buttons-area-input">
        <button className="button-file">Criar Arquivo</button>
      </div>
    </div>
  );
}
