import "./style.css";
import { useState } from "react";

export default function PostFile() {
  const [message, setMessage] = useState("");
  const [textmessage, setText] = useState("");
  let speech = new SpeechSynthesisUtterance();

  async function loadfile(file) {
    let text = await file.text();
    return text;
  }
  const change = () => {
    setText(loadfile(this.files[0]));
    setMessage("arquivo enviado");
  };
  const speak = () => {
    speech.text = textmessage;
    return speechSynthesis.speak(speech);
  };
  /*useEffect(() => {
         changeFile = change();
        
  });*/

  return (
    <div className="Container-post">
      <label for="input-file" className="label-file">
        Selecionar um arquivo
      </label>
      <input
        type="file"
        id="input-file"
        className="input-file"
        onChange={change}
      />
      <span className="text-file">{message}</span>
      <button className="button-file" onClick={speak}>
        Criar Arquivo
      </button>
    </div>
  );
}
