import "./style.css";
import { useEffect, useState } from "react";

export default function PostFile() {
  const [message, setMessage] = useState("");
  const [textmessage, setText] = useState("");
  const speech = new SpeechSynthesisUtterance();
  const [msg, getSpeech] = useState(speech);

  useEffect(() => {
    async function loadFile(file) {
      //let text = await file.text();
      setText(await file.text);
      msg.text = "text";
    }
  });
  const change = () => {
    return setMessage("arquivo enviado");
  };
  return (
    <div className="Container-post">
      <label for="input-file" className="label-file">
        Selecionar um arquivo
      </label>
      <input
        type="file"
        id="input-file"
        className="input-file"
        onChange={change}
      />
      <span className="text-file">{message}</span>
      <button className="button-file">Criar Arquivo</button>
    </div>
  );
}
