import "./style.css";
import { useState } from "react";

export default function PostFile() {
  const [message, setMessage] = useState("");

  const change = () => {
    return setMessage("arquivo enviado");
  };
  return (
    <div className="Container-post">
      <label for="input-file" className="label-file">
        Selecionar um arquivo
      </label>
      <input
        type="file"
        id="input-file"
        className="input-file"
        onChange={change}
      />
      <span className="text-file">{message}</span>
    </div>
  );
}
